mapper.select:
SELECT COUNT(*)
FROM SANCTION JOIN MEMBER USING(MEMBER_NUM)
WHERE 1=1

    AND MEMBER_ID = #{inqKeyword}


    AND MEMBER_NICKNAME = #{inqKeyword}

SELECT MEMBER_NUM FROM MEMBER
WHERE 1=1

    AND MEMBER_ID = #{inqKeyword}


    AND MEMBER_NICKNAME = #{inqKeyword}

mapper.update:
UPDATE REPORT
SET REPORT_SOLVED = 1
WHERE REPORT_ID = #{reportId}
